import React from 'react';

class Decode extends React.Component {
    state = {
        imageArray: null,
        imageURI: null,
        receivedMsg: ''
    }
    getCryptoImage = async (e) => {
        e.preventDefault();
        const res = await fetch('http://localhost:5000/decode', {
            headers: {
                'Content-Type': 'application/json'
            },
            method: 'POST',
            body: JSON.stringify({ imageArray: this.arrToBase64(this.state.imageArray) })
        })
        const data = await res.blob();
        const url = URL.createObjectURL(data);
        this.setState({ receivedImg: url })
    }
    render() {
        return (
            <div id="file-container">
                <form onSubmit={this.getCryptoImage}>
                    <p>Please select an image to cryptify</p>
                    <input type="file" accept="image/*" onChange={this.getImage} name="imageFile" required/>
                    <div id="product-display" className="hidden">
                        <h3>Image Preview</h3>
                        <img src={this.state.imageURI} alt="Cannot render"/>
                    </div>
                    <div>
                        <textarea placeholder="Enter in the message you wish to cryptify within in the image here..." name="msg" required value={this.state.msg} onChange={this.updateMsg}></textarea>
                    </div>
                    <input type="submit" value="Decodify"/>
                </form>

                { this.state.receivedMsg.length > 0 &&
                <div>
                    <h3>{ this.state.receivedMsg }</h3>
                </div> }
            </div>   
        );
    }
}

export default Decode;
