import React from 'react';

class Encode extends React.Component {
    state = {
        imageURI: null
    }
    getImage = (e) => {
        const reader = new FileReader();
        const imageFile = e.target.files[0];

        if (imageFile) {
            reader.readAsDataURL(imageFile);
            document.getElementById('product-display').classList.remove('hidden');
        }

        reader.addEventListener('load', () => {
            this.setState({
                imageURI: reader.result
            });
        });
    }
    getCryptoImage = (e) => {
        e.preventDefault();
        fetch('http://localhost:3001/hi', {
            method: 'POST',
            origin: 'http://localhost:3001/'
        })
        .then((res) => console.log(res))
        .catch((err) => console.log(err))
    }
    render() {
        return (
            <div id="file-container">
                <form onSubmit={this.getCryptoImage}>
                    <p>Please select an image to cryptify</p>
                    <input type="file" accept="image/*" onChange={this.getImage} required/>
                    <div id="product-display" className="hidden">
                        <h3>Image Preview</h3>
                        <img src={this.state.imageURI} alt="Cannot render"/>
                    </div>
                    <div>
                        <textarea placeholder="Enter in the message you wish to cryptify within in the image here..." required></textarea>
                    </div>
                    <input type="submit" value="Cryptify"></input>
                </form>
            </div>   
        );
    }
}

export default Encode;
