import React from 'react';

class Encode extends React.Component {
    state = {
        imageURI: null
    }
    getImage = (e) => {
        const reader = new FileReader();
        const imageFile = e.target.files[0];

        if (imageFile) {
            reader.readAsDataURL(imageFile);
        }

        reader.addEventListener('load', function() {
            this.setState({
                imageURI: reader.result
            });
            document.getElementById('image-preview').classList.remove('image-preview');
            document.getElementById('image-preview').src = reader.result;
        });
    }
    render() {
        return (
            <div>
                <input type="file" accept="image/*" onChange={this.getImage}></input>
                <div id="preview-display" className="hidden">
                    <img src="" alt="Cannot render image" id="image-preview"/>
                </div>
            </div>   
        );
    }
}

export default Encode;
