import React from 'react';
import Encode from './Encode'
import Decode from './Decode'
import { BrowserRouter, Route, NavLink, Switch } from 'react-router-dom'
import Display from './Display';
import Redirect from './Redirect';
import anime from 'animejs/lib/anime.es'

class App extends React.Component {
  state = {
    receivedMsg: '',
    receivedImg: ''
  }
  // Way for Encode component to pass image to App component
  encodeImg = (receivedImg) => {
    this.setState({
      receivedImg: receivedImg,
    });
  }
  // Way for Decode component to pass message to App component
  decodeMsg = (receivedMsg) => {
    this.setState({
      receivedMsg: receivedMsg,
    });
  }
  resetState = () => {
    this.setState({
      receivedMsg: '',
      receivedImg: ''
    })
  }
  // Have section 1 as main-display for now.. May change completely
  render() {
    return (
      <BrowserRouter>
        <div id="App">
          <section className="main-display">
            <h1>CryptoImage</h1>
            <p><em>Fun way to encrypt messages to friends!</em></p>
            <NavLink to="/encode" onClick={this.resetState}>Encode</NavLink>
            <NavLink to="/decode" onClick={this.resetState}>Decode</NavLink>
            <div id="main">
              <Switch>
                <Route path="/encode" render={(props) => <Encode {...props} encodeImg={this.encodeImg}/>}/>
                <Route path="/decode" render={(props) => <Decode {...props} decodeMsg={this.decodeMsg}/>}/>
                <Route path="/*" component={Redirect}/>
              </Switch>
            </div>
          </section>
          <Display receivedImg={this.state.receivedImg} receivedMsg={this.state.receivedMsg}/>
        </div>
      </BrowserRouter>
    )
  }
  componentDidMount() {
    const mainDisplay = document.querySelector('.main-display');
    const mainDisplayObj = {
        height: mainDisplay.offsetHeight
    }

    /* anime({
        targets: mainDisplayObj,
        height: window.innerHeight,
        update: function() {
            mainDisplay.style.height = mainDisplayObj.height;
        }
    }); */



    /* const mainDisplay = document.querySelector('.main-display');
    const mainDisplayColorsObj = {
      leftR: 95,
      leftG: 160,
      leftB: 245
    }
    // mainDisplay.style.color = '#fff';
    anime({
      targets: mainDisplayColorsObj,
      leftR: 126,
      leftG: 163,
      leftB: 197,
      update: function() {
        mainDisplay.style.background = linear-gradient(to right, rgb(leftR,leftG,leftB), rgb(126,163,197));;
      }
    }); */
  }
}

export default App;
