import React from 'react';

class Encode extends React.Component {
    state = {
        imageFile: null,
        imageURI: null,
        msg: null
    }
    getImage = (e) => {
        const reader = new FileReader();
        const imageFile = e.target.files[0];

        if (imageFile) {
            reader.readAsDataURL(imageFile);
            document.getElementById('product-display').classList.remove('hidden');
        }

        reader.addEventListener('load', () => {
            this.setState({
                imageURI: reader.result,
                imageFile: imageFile
            });
        });
    }
    getCryptoImage = async (e) => {
        e.preventDefault();
        await this.setState({
            msg: document.querySelector('textarea[name="msg"]').value
        });

        console.log(this.state.imageFile)

        /* await fetch('http://localhost:5000/encode-image', {
            method: 'POST',
            headers: {
                'Content-Type': 'multipart/form-data'
            },
            body: this.state.imageFile
        })
        .then((res) => res.json())
        .then((data) => {
            console.log(data);
        }); */

        /* await fetch('http://localhost:5000/encode-msg', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ msg: this.state.msg })
        })
        .then((res) => res.text())
        .then((data) => {
            console.log(data);
        }); */
    }
    render() {
        return (
            <div id="file-container">
                <form onSubmit={this.getCryptoImage}>
                    <p>Please select an image to cryptify</p>
                    <input type="file" accept="image/*" onChange={this.getImage} name="imageFile" required/>
                    <div id="product-display" className="hidden">
                        <h3>Image Preview</h3>
                        <img src={this.state.imageURI} alt="Cannot render"/>
                    </div>
                    <div>
                        <textarea placeholder="Enter in the message you wish to cryptify within in the image here..." name="msg" required></textarea>
                    </div>
                    <input type="submit" value="Cryptify"/>
                </form>
            </div>   
        );
    }
}

export default Encode;
