import React from 'react';
import Encode from './Encode'
import Decode from './Decode'
import { BrowserRouter, Route, NavLink, Switch } from 'react-router-dom'
import Display from './Display';
import Redirect from './Redirect';
import anime from 'animejs/lib/anime.es'

class App extends React.Component {
  state = {
    receivedMsg: '',
    receivedImg: ''
  }
  // Way for Encode component to pass image to App component
  encodeImg = (receivedImg) => {
    this.setState({
      receivedImg: receivedImg,
    });
  }
  // Way for Decode component to pass message to App component
  decodeMsg = (receivedMsg) => {
    this.setState({
      receivedMsg: receivedMsg,
    });
  }
  resetState = () => {
    this.setState({
      receivedMsg: '',
      receivedImg: ''
    })
  }
  revertMainDisplay = (originalHeight) => {
    const mainDisplay = document.querySelector('.main-display');
    const mainDisplayObj = {
        height: mainDisplay.offsetHeight + 'px'
    }
    anime({
        targets: mainDisplayObj,
        height: originalHeight + 'px',
        duration: 1000,
        update: function() {
            mainDisplay.style.height = mainDisplayObj.height;
        }
    });
    // document.querySelector('.main-display').style.height = 'initial';
  }
  // Have section 1 as main-display for now.. May change completely
  render() {
    return (
      <BrowserRouter>
        <div id="App">
          <section className="main-display">
            <h1>CryptoImage</h1>
            <p><em>Fun way to encrypt messages to friends!</em></p>
            <NavLink to="/encode" onClick={this.resetState}>Encode</NavLink>
            <NavLink to="/decode" onClick={this.resetState}>Decode</NavLink>
            <div id="main">
              <Switch>
                <Route path="/encode" render={(props) => <Encode {...props} encodeImg={this.encodeImg} revertMainDisplay={this.revertMainDisplay}/>}/>
                <Route path="/decode" render={(props) => <Decode {...props} decodeMsg={this.decodeMsg} revertMainDisplay={this.revertMainDisplay}/>}/>
                <Route path="/*" component={Redirect}/>
              </Switch>
            </div>
          </section>
          <section className="border">
            <svg className="border-curve" width="1038" height="37" viewBox="0 0 1038 37" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path fill="#fff" d="M0 0h1038v37H0z"/>
              <path d="M173 21.82C112 2.78 56 2.264 0 19.246V37h1038V19.247c-61 19.04-117 19.554-173 2.572-61-19.04-117-19.554-173-2.572-61 19.04-117 19.554-173 2.572-61-19.04-117-19.554-173-2.572-61 19.04-117 19.554-173 2.572z"/>
            </svg>
          </section>
          <Display receivedImg={this.state.receivedImg} receivedMsg={this.state.receivedMsg}/>
        </div>
      </BrowserRouter>
    )
  }
  componentDidMount() {
    // mainDisplay.height = window.innerHeight;
    /* const mainDisplay = document.querySelector('.main-display');
    const mainDisplayColorsObj = {
      leftR: 95,
      leftG: 160,
      leftB: 245
    }
    // mainDisplay.style.color = '#fff';
    anime({
      targets: mainDisplayColorsObj,
      leftR: 126,
      leftG: 163,
      leftB: 197,
      update: function() {
        mainDisplay.style.background = linear-gradient(to right, rgb(leftR,leftG,leftB), rgb(126,163,197));;
      }
    }); */
  }
}

export default App;
