import React from 'react';

class Encode extends React.Component {
    state = {
        imageArray: null,
        imageURI: null,
        // imageFile: null,
        msg: null
    }
    getImage = (e) => {
        const readerArray = new FileReader(), readerURI = new FileReader();
        const imageFile = e.target.files[0];

        if (imageFile) {
            readerArray.readAsArrayBuffer(imageFile);
            readerURI.readAsDataURL(imageFile);
            document.getElementById('product-display').classList.remove('hidden');
        }

        readerArray.addEventListener('load', () => {
            this.setState({
                imageArray: readerArray.result,
            });
        });

        readerURI.addEventListener('load', () => {
            this.setState({
                imageURI: readerURI.result,
            });
        });
    }
    getCryptoImage = async (e) => {
        e.preventDefault();
        await this.setState({
            msg: document.querySelector('textarea[name="msg"]').value
        });
        // const dotIndex = this.state.imageFile.name.lastIndexOf('.');
        // const fileType = this.state.imageFile.name.substring(dotIndex)
        await fetch('http://localhost:5000/encode', {
            method: 'POST',
            body: { msg: this.state.msg, imageArray: this.state.imageArray }
        })
        .then((res) => res.json())
        .then((data) => {
            console.log(data);
        });
    }
    render() {
        return (
            <div id="file-container">
                <form onSubmit={this.getCryptoImage}>
                    <p>Please select an image to cryptify</p>
                    <input type="file" accept="image/*" onChange={this.getImage} name="imageFile" required/>
                    <div id="product-display" className="hidden">
                        <h3>Image Preview</h3>
                        <img src={this.state.imageURI} alt="Cannot render"/>
                    </div>
                    <div>
                        <textarea placeholder="Enter in the message you wish to cryptify within in the image here..." name="msg" required></textarea>
                    </div>
                    <input type="submit" value="Cryptify"/>
                </form>
            </div>   
        );
    }
}

export default Encode;
